        if (zygoteServer != null) {//断开socket连接
            zygoteServer.closeServerSocket();
        }
    }

    // We're in the child process and have exited the select loop. Proceed to execute the
    // command.
    if (caller != null) {//非System server子进程，在这里调用，子进程开始自己的任务
        caller.run();
    }
}

